import logo from './logo.svg';
import './App.css';
import { Unity, useUnityContext } from "react-unity-webgl";

function App() {
  const { unityProvider } = useUnityContext({
    loaderUrl: "/BirdGangGame/Build/BirdGangGame.loader.js",
    dataUrl: "/BirdGangGame/Build/BirdGangGame.data",
    frameworkUrl: "/BirdGangGame/Build/BirdGangGame.framework.js",
    codeUrl: "/BirdGangGame/Build/BirdGangGame.wasm",
  });

  var container = document.querySelector("#unity-container");
  var canvas = document.querySelector("#unity-canvas");
  var loadingSpinner = document.querySelector("#unity-loading-spinner");
  var loadingBar = document.querySelector("#unity-loading-bar");
  var loadingBarBar = document.querySelector("#unity-loading-bar-b");
  var fullscreenButton = document.querySelector("#unity-fullscreen-button");

  if (/iPhone|iPad|iPod|Android/i.test(navigator.userAgent)) {
    container.className = "unity-mobile";
    config.devicePixelRatio = 1;
    Swal.fire({
      title: "You're on mobile!",
      text: 'WebGL is not supported on mobile.. Continue at your own risk',
      icon: 'warning',
      confirmButtonText: 'I understand'
    });
  }

  loadingSpinner.style.display = "block";
  loadingBar.style.display = "block";

  var script = document.createElement("script");
  script.src = loaderUrl;
  script.onload = () => {
    createUnityInstance(canvas, config, (progress) => {
      loadingBarBar.style.width = 100 * progress + "%";
    }).then((unityInstance) => {
      window.unityInstance = unityInstance;
      loadingBar.style.display = "none";
      loadingSpinner.style.display = "none";
      fullscreenButton.onclick = () => {
        unityInstance.SetFullscreen(1);
      };
    }).catch((message) => {
      alert(message);
    });
  };
  document.body.appendChild(script);

  return <Unity unityProvider={unityProvider} />;
}



export default App;
